import React from "react";
import { Text, View, StyleSheet, Button } from "react-native";
import ProductCarousel from "./components/ProductCarousel";
import { TouchableHighlight } from "react-native-gesture-handler";

const ProductScreen = ({ navigation }) => {
  return (
    <View>
      <ProductCarousel navigation={navigation} />
      {/* <View
        title
        style={[
          styles.itemContainer,
          {
            backgroundColor: navigation.getParam("productColor", "#000"),
          },
        ]}>
      </View> */}
      <TouchableHighlight style={styles.wishlist}>
        <Button title="Add to Wishlist" />
      </TouchableHighlight>
      <View style={styles.chosenItem}>
        <Text style={{ fontSize: "17px" }}>
          {JSON.stringify(
            navigation.getParam("productName", "Error getting product name")
          )}
        </Text>
        <Text style={{ fontSize: "17px" }}>$0.00</Text>
      </View>
      <View style={styles.itemInteractions}>
        <TouchableHighlight style={styles.cart}>
          <Button title="Add to Cart"></Button>
        </TouchableHighlight>
        <TouchableHighlight style={styles.descrip}>
          <Button title="Product Description"></Button>
        </TouchableHighlight>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  itemContainer: {
    justifyContent: "flex-end",
    borderRadius: 5,
    padding: 10,
    height: 400,
  },
  itemName: {
    fontSize: 16,
    color: "#000",
    fontWeight: "600",
  },
  wishlist: {
    flexDirection: "row",
    justifyContent: "flex-end",
    top: 20,
  },
  itemInteractions: {
    textAlign: "center",
    marginTop: "110%",
    padding: "10%",
    height: "100%",
  },
  descrip: {
    backgroundColor: "lightblue",
    padding: "1%",
    borderRadius: 3,
    width: "100%",
    marginVertical: 25,
  },
  cart: {
    backgroundColor: "lightblue",
    padding: "2%",
    borderRadius: 3,
    width: "100%",
  },
  chosenItem: {
    top: 430,
    flexDirection: "row",
    justifyContent: "space-around",
  },
});

export default ProductScreen;
